import { PlaceholderResourceEmptySearch } from './Placeholder'

export const ContentValidateFilter = ({
  searchText,
  listLength,
  children,
}: {
  searchText?: string,
  listLength: number,
  children: React.ReactNode,
}) => {
  if (!listLength && searchText) return <PlaceholderResourceEmptySearch />
  return <>{children}</>
}

