import { Language } from './types'

export class TranslationError extends Error {
  constructor(message: string) {
    super(`Translation error: ${message}.`)
  }
}

export class MissingDefaultLanguageTranslation extends TranslationError {
  constructor(defaultLanguage: Language) {
    super(`missing translation for the default language: ${defaultLanguage}`)
  }
}

export class TranslationConfigError extends TranslationError {
  constructor(message: string) {
    super(message)
  }
}
